    private void realizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_realizarActionPerformed
        double numeroGrados=0;
        double operacion;
        
        if(gradosC.isSelected()){
           numeroGrados=Integer.parseInt(inputGrados.getText());
           if(lista.getSelectedItem().equals("Kelvin")){
               operacion=numeroGrados+273.15;
               outputGrados.setText(operacion+"");
           }else if(lista.getSelectedItem().equals("Farenheit")){
                operacion=((numeroGrados*9)/5)+32;
                outputGrados.setText(operacion+"");
           }
           
        }else if(gradosF.isSelected()){
           numeroGrados=Integer.parseInt(inputGrados.getText());
           if(lista.getSelectedItem().equals("Celsius")){
               operacion=((numeroGrados-32)*5)/9;
               outputGrados.setText(operacion+"");
           }else if(lista.getSelectedItem().equals("Kelvin")){
               operacion=(((numeroGrados-32)*5)/9)+273.15;
               outputGrados.setText(operacion+"");
           }
           
        }else{
           numeroGrados=Integer.parseInt(inputGrados.getText());
           if(lista.getSelectedItem().equals("Celsius")){
                operacion=numeroGrados-273.15 ;
                outputGrados.setText(operacion+"");
           }else if(lista.getSelectedItem().equals("Farenheit")){
                operacion=(((numeroGrados-273.15)*9)/5)+32 ;
                outputGrados.setText(operacion+"");
           }
        }
        
        
        
    }//GEN-LAST:event_realizarActionPerformed

