    private void gradosKItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_gradosKItemStateChanged
        gradosIzquierda.setText("ºK");
        jPanel1.setBorder(createTitledBorder("Convierte de º Kelvin"));


    }//GEN-LAST:event_gradosKItemStateChanged

    private void listaItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_listaItemStateChanged
        if(lista.getSelectedItem().equals("Celsius")){
            jPanel2.setBorder(createTitledBorder("A º Celsius"));
            gradosDerecha.setText("º C");
            outputGrados.setText(null);
        }else if(lista.getSelectedItem().equals("Kelvin")){
            jPanel2.setBorder(createTitledBorder("A º Kelvin"));
            gradosDerecha.setText("º K");
            outputGrados.setText(null);
        }else{
            jPanel2.setBorder(createTitledBorder("A º Farenheit"));
            gradosDerecha.setText("º F");
            outputGrados.setText(null);
        }
        
    }//GEN-LAST:event_listaItemStateChanged

