    private void simularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_simularActionPerformed
        //Coges la longitud del tamaño de la lista
        // seleccionas 3 temas con math random
        int temasTotales=Integer.parseInt(inputTemas.getText());
        int tema1;
        int tema2;
        int tema3;
        do{
            tema1=(int)(Math.random()*temasTotales);
            tema2=(int)(Math.random()*temasTotales);
            tema3=(int)(Math.random()*temasTotales);
        }while(tema1==tema2 || tema1==tema3 || tema2==tema3);
        area2.add(tema2+"");
        area2.add(tema1+"");
        area2.add(tema3+"");
    }//GEN-LAST:event_simularActionPerformed

    private void nuevoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_nuevoActionPerformed
        area1.removeAll();
        area2.removeAll();
        probabilidad.setText("");
        inputTemas.setText("");
    }//GEN-LAST:event_nuevoActionPerformed

    private void generarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_generarActionPerformed
        int temasTotales=Integer.parseInt(inputTemas.getText());
        double temasPreparados=(double)area1.getItemCount();
        double proporcion=(temasPreparados/temasTotales)*100;
        probabilidad.setText(proporcion+"");
    }//GEN-LAST:event_generarActionPerformed

