    /**
     * @param args the command line arguments
     */
    public void probabilidad(){
        int total = Integer.parseInt(TextFieldNuevoTema.getText());
        double suma=0,prob;
        for(int z=0 ; z<1000 ; z++){
            Bombo bombo=new Bombo(total);
            boolean estado=false;
            int j=0;    
            do{
                int tema=bombo.sacaBola()+1;
                int i=0;
                do{
                    if(tema==Integer.parseInt(area1.getItem(i))){
                    suma++;
                    estado=true;
                    }
                    i++;
                }while(i<area1.getItemCount() && !estado);
                j++;
            }while(j<3 && !estado);
        }
        prob=suma/1000*100;
        probabilidad.setText(""+prob);
    }
    
    public void obtenertemas(){
        int total = Integer.parseInt(TextFieldNuevoTema.getText()+1);
        Bombo bombo=new Bombo(total);
        area2.removeAll();
        for(int i=1 ; i<=3 ; i++){
            area2.add(""+bombo.sacaBola());
        }
    }
    
